export declare const typeDefs = "\n    enum NoteStatus {\n        NOTES\n        ARCHIVED\n        TRASH\n    }\n\n    type User {\n        id: Int!\n        username: String!\n        email: String\n        profileImage: String\n        firstName: String\n        secondName: String\n        description: String!\n        lastLogin: String!\n        creationDate: String!\n        modifiedDate: String!\n    }\n\n    type ChecklistItem {\n        id: Int!\n        text: String!\n        isCompleted: Boolean!\n    }\n\n    type Todo {\n        id: Int!\n        title: String!\n        content: String\n        items: [ChecklistItem]\n        backgroundImage: String\n        status: NoteStatus!\n        createdAt: String!\n        updatedAt: String!\n        userId: Int!\n    }\n\n    type LoginResponse {\n        token: String!\n        user: User!\n    }\n\n    type SuccessMessage {\n        message: String!\n    }\n\n    type DeleteResponse {\n        id: Int!\n        success: Boolean!\n    }\n\n    input CreateTodoItemInput {\n        text: String!\n    }\n\n    input CreateTodoInput {\n        title: String!\n        content: String\n        items: [CreateTodoItemInput]\n    }\n\n    input UpdateTodoInput {\n        title: String\n        content: String\n    }\n\n    input UpdateProfileInput {\n        username: String\n        email: String\n        profileImage: String\n        firstName: String\n        secondName: String\n        description: String\n    }\n\n    # --- Query ---\n\n    type Query {\n        me: User\n        todos(status: NoteStatus): [Todo!]!\n    }\n\n    # --- Mutation ---\n\n    type Mutation {\n        # Auth\n        login(email: String!, password: String!): LoginResponse!\n        signup(email: String!, password: String!): SuccessMessage!\n        logout: SuccessMessage!\n\n        # Profile\n        updateProfile(input: UpdateProfileInput!): User!\n\n        # Todos\n        createTodo(input: CreateTodoInput!): Todo!\n        updateTodo(id: Int!, input: UpdateTodoInput!): Todo!\n        changeTodoStatus(id: Int!, newStatus: NoteStatus!): Todo!\n        deleteTodo(id: Int!): DeleteResponse!\n\n        # Checklist Actions\n        toggleChecklistItem(todoId: Int!, itemId: Int!): Todo!\n        updateChecklistItemText(todoId: Int!, itemId: Int!, text: String!): Todo!\n        uncheckAllItems(id: Int!): Todo!\n\n        # Background\n        updateTodoBackground(id: Int!, backgroundImage: String!): Todo!\n    }\n";
