export declare const typeDefs = "\n    type User {\n        id: Int!\n        username: String!\n        email: String!\n        firstName: String\n        secondName: String\n        description: String\n        profileImage: String\n        creationDate: String\n        modifiedDate: String\n    }\n\n    type SuggestedUser {\n        id: Int!\n        username: String!\n        description: String\n        photo: String\n    }\n\n    type Group {\n        id: Int!\n        title: String!\n        photo: String\n        membersCount: Int!\n    }\n\n    type Post {\n        id: Int!\n        authorId: Int!\n        author: User!\n        title: String!\n        content: String!\n        image: String\n        creationDate: String!\n        modifiedDate: String!\n        likesCount: Int!\n        commentsCount: Int!\n        likedByUsers: [User]\n    }\n\n    type Comment {\n        id: Int!\n        postId: Int!\n        authorId: Int!\n        text: String!\n        creationDate: String!\n        modifiedDate: String!\n    }\n\n    type Like {\n        id: Int!\n        userId: Int!\n        postId: Int!\n        creationDate: String!\n    }\n\n    type LoginResponse {\n        token: String!\n        user: User!\n    }\n\n    input UpdateProfileInput {\n        username: String\n        email: String\n        firstName: String\n        secondName: String\n        description: String\n        profileImage: String\n    }\n\n    input CreatePostInput {\n        title: String!\n        content: String!\n        image: String\n    }\n\n    input CreateGroupInput {\n        title: String!\n        photo: String\n    }\n\n    type Query {\n        me: User\n        suggestedUsers: [SuggestedUser]\n\n        allGroups: [Group]\n        group(id: Int!): Group\n\n        allPosts: [Post]\n        post(id: Int!): Post\n        postComments(postId: Int!): [Comment]\n        allComments: [Comment]\n\n        # /me/*\n        mePosts: [Post]\n        meComments: [Comment]\n        meCommentsCount: Int!\n        meLikes: [Like]\n        meLikesCount: Int!\n    }\n\n    type Mutation {\n        # Auth\n        login(email: String!, password: String!): LoginResponse\n        signup(email: String!, password: String!): Boolean!\n        logout: Boolean!\n\n        # User\n        updateProfile(input: UpdateProfileInput!): User\n\n        # Posts\n        createPost(input: CreatePostInput!): Post\n        updatePost(id: Int!, input: CreatePostInput!): Post\n        deletePost(id: Int!): Boolean!\n        likePost(postId: Int!): Post\n        dislikePost(postId: Int!): Post\n\n        # Comments\n        createComment(postId: Int!, text: String!): Comment\n        updateComment(id: Int!, text: String!): Comment\n        deleteComment(id: Int!): Boolean!\n\n        # Groups\n        createGroup(input: CreateGroupInput!): Group\n        updateGroup(id: Int!, input: CreateGroupInput!): Group\n        deleteGroup(id: Int!): Boolean!\n    }\n";
